var pars = require('svg-path-parser')

module.exports = function(d, dec) {
	var r = ''
	var data = pars(d)
	data.forEach(function(d) {
		var p = []
		for(k in d) { if(k !== 'code' && k !== 'command' && k !== 'relative' ) { p.push(Math.round(d[k]* dec)/dec )} }
		var s = d.code + ' '
		p.forEach(function(d,i) {
			s = s + d
			if(isEven(i)) { s = s + ',' } else { s = s + ' ' }
		})
		r = r + s + ' '
	})
	return r
}

function isEven(n) {
   return n % 2 == 0;
}
